import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [message, setmessage] = useState("");
  const [tasks, settasks] = useState([]);

  const handleclick = async () => {
    try {
      const res = await fetch("http://localhost:8000/api/addtodo", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message,
        }),
      });
      const data = res.json();
      if (data) {
        alert("new task added succesfully");
      }
    } catch (err) {
      console.log(err);
    }
  };

  const gettasks = async () => {
    const response = await fetch("http://localhost:8000/api/getodo", {
      method: "GET",
    });
    const data = await response.json();
    settasks(data);
  };

  useEffect(() => {
    gettasks();
  }, []);

  return (
    <div className="App">
      <div className="input">
        <input
          type="text"
          name="message"
          value={message}
          onChange={(e) => setmessage(e.target.value)}
          placeholder="Add Your Work here"
        />

        <button onClick={handleclick}>ADD </button>
      </div>
      <div className="show">
        <ul>
          {tasks.map((task) => (
            <li>{task.message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
