import { useState } from "react";
import "./App.css";

function App() {
  const [message, setmessage] = useState("");

  const handleclick = async () => {
    event.preventDefault();
    try 
    {
      const res = await fetch("http://localhost:8000/api/addtodo", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message,
        }),
      });
      if (res.status === "ok") {
        alert("new task added succesfully");
      }
    } 
    catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="App">
      <div className="input">
        <input
          type="text"
          onChange={(e) => setmessage(e.target.value)}
          placeholder="Add Your Work here"
        />
        <span onClick={handleclick}>ADD</span>
      </div>
      <div className="show">

      </div>
    </div>
  );
}

export default App;
