import { useEffect, useState } from 'react';
import "./App.css";

function App() {
  const [message, setmessage] = useState("");

  const handleclick = async () => {
    try {
      const res = await fetch("http://localhost:8000/api/addtodo", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
           message
          }),
      });
      if (res.status === "ok") {
        alert("new task added succesfully");
      }
    } catch (err) {
      console.log(err);
    }
  };

  const getUsers = async ()=>{
    const response = await fetch('http://localhost:8000/api/getodo',{
      method:'GET',
    })
   const data = await response.json();
   setUsers(data);
  }

  useEffect(()=>{
    getUsers();
  },[])

  return (
    <div className="App">
      <div className="input">
        <input
          type="text"
          name="message"
          value={message}
          onChange={(e) => setmessage(e.target.value)}
          placeholder="Add Your Work here"
        />
        <span>
          <button onClick={handleclick}>ADD </button>
        </span>
      </div>
      <div className="show">{message}</div>
    </div>
  );
}

export default App;
