import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [item, setitem] = useState("");
  const [tasks, settasks] = useState([]);

  const addtasks = async () => {
    try {
      const res = axios.post("http://localhost:8000/api/addtask", {
        message: item
      });
      const data = res.data;
      setitem('');
      console.log(data)
     // settasks(prev => [...prev, data]);
      alert("new task added succesfully");


    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    const gettasks = async () => {
      try {
        const response = axios.get("http://localhost:8000/api/gettask");
        const data = await response.data;
        console.log(data)
        settasks(data);
      } catch (err) {
        console.log(err);
      }
    };
    gettasks();
  }, []);

  return (
    <div className="App">
      <div className="input">
        <input
          type="text"
          name="message"
          value={item}
          onChange={(e) => setitem(e.target.value)}
          placeholder="Add Your Work here"
        />

        <button onClick={addtasks}>ADD </button>
      </div>
      <div className="show">
        <p>{item}</p>

        {  tasks.map((task) => (
          <p>{task.message}</p>
        ))}
      </div>
    </div>
  );
}

export default App;
