import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [message, setmessage] = useState("");
  const [tasks, settasks] = useState([]);

  const addtasks = async () => {
    try {
      const res = axios.post("http://localhost:8000/api/addtask", {
        message: message,
      });
      //settasks(prev => [...prev, res.data]);
     // const data = res.json();

        alert("new task added succesfully");

    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    const gettasks = async () => {
      try {
        const response = axios.get("http://localhost:8000/api/gettask");
       // const data = await response.json();
        const data = await response.json();
        settasks(data);
      } catch (err) {
        console.log(err);
      }
    };
    gettasks();
  }, []);

  return (
    <div className="App">
      <div className="input">
        <input
          type="text"
          name="message"
          value={message}
          onChange={(e) => setmessage(e.target.value)}
          placeholder="Add Your Work here"
        />

        <button onClick={addtasks}>ADD </button>
      </div>
      <div className="show">
        {tasks.map((task) => (
          <p>{task.message}</p>
        ))}
      </div>
    </div>
  );
}

export default App;
